import React , {useRef , useState} from 'react'
import styles from './Checkout.module.css'


const Checkout = (props) =>{
    const [formIsValid, setFormIsValid] = useState({
        name: true,
        street: true,
        postalcode: true,
        city: true
    })

    const nameRef = useRef()
    const streetRef = useRef()
    const postalcodeRef = useRef()
    const cityRef = useRef()


    const onConfirmHandler = (event) => {
        event.preventDefault()
        const enteredName = nameRef.current.value
        const enteredStreet = streetRef.current.value
        const enteredPostalCode = postalcodeRef.current.value
        const enteredCity = cityRef.current.value

        const nameIsValid = /^\S+$/.test(enteredName)
        const streetIsValid = /^\S+$/.test(enteredStreet)
        const postalcodeIsValid = /^\S+$/.test(enteredPostalCode)
        const cityIsValid = /^\S+$/.test(enteredCity)

        setFormIsValid({
            name: nameIsValid,
            street: streetIsValid,
            postalcode: postalcodeIsValid,
            city: cityIsValid 
        })
    } 

 

    return(
        <form className={styles.form} onSubmit={onConfirmHandler}>
        <div className={styles.control}>
          <label htmlFor='name'>Your Name</label>
          <input type='text' id='name' ref={nameRef}/>
        </div>
        <div className={styles.control}>
          <label htmlFor='street'>Street</label>
          <input type='text' id='street'  ref={streetRef}/>
        </div>
        <div className={styles.control}>
          <label htmlFor='postal'>Postal Code</label>
          <input type='text' id='postal' ref={postalcodeRef}/>
        </div>
        <div className={styles.control}>
          <label htmlFor='city'>City</label>
          <input type='text' id='city' ref={cityRef}/>
        </div>
        <div className={styles.actions}>
          <button type='button' onClick={props.onCancel}>
            Cancel
          </button>
          <button className={styles.submit}>Confirm</button>
        </div>
      </form>
    )
}

export default Checkout
