  console.log(cartIsShown)

  function showCartHandler(){
    setCartIsShown(true)
  }

  function hideCartHandler(){
    // setCartIsShown(false)
    console.log('working')
  }

  return (
    <Fragment>
      {cartIsShown && <Cart onHideCart={hideCartHandler} />}
      <Header onShowCart={showCartHandler}/>
      <main>
        <Meals />
      </main>
    </Fragment>
  );
}

export default App;
