import Input from '../../UI/Input'
import styles from './MealItemForm.module.css'

export default function MealItemForm(props){
    return(
        <form className=''>
            <Input label="Amount" input={{
                id: 'amount',
                type: 'number',
                min: '1',
                max: '5',
                step: '1',
                defaultValue: '1'
            }}/>
            <button>+ Add</button>
        </form>
    )
}
